mapper.select:
SELECT P_IDX, PAGE_NM, M_ID, SCORE, CHK_OPT, MEMO
  FROM tb_p_data
 WHERE PAGE_NM = #{code}

SELECT E_IDX, PAGE_NM, M_ID, SCORE, CHK_OPT, MEMO
  FROM tb_e_data
 WHERE PAGE_NM = #{code}

SELECT S_IDX, PAGE_NM, M_ID, SCORE, CHK_OPT, MEMO
  FROM tb_s_data
 WHERE PAGE_NM = #{code}

SELECT G_IDX, PAGE_NM, M_ID, SCORE, CHK_OPT, MEMO
  FROM tb_g_data
 WHERE PAGE_NM = #{code}

mapper.update:
UPDATE tb_p_data
   SET SCORE=#{score}, CHK_OPT=#{check}
 WHERE PAGE_NM = #{code};

UPDATE tb_e_data
   SET SCORE=#{score}, CHK_OPT=#{check}
 WHERE PAGE_NM = #{code};

UPDATE tb_s_data
   SET SCORE=#{score}, CHK_OPT=#{check}
 WHERE PAGE_NM = #{code};

UPDATE tb_g_data
   SET SCORE=#{score}, CHK_OPT=#{check}
 WHERE PAGE_NM = #{code};
