mapper.select:
SELECT COUNT(*) FROM tb_cd_mst WHERE del_yn = 'N' AND grp_cd =
#{detail_grp_cd}
#{grp_cd}
AND sort_no > 0

mapper.update:
UPDATE tb_cd_mst
   SET del_yn = 'Y',
       del_ts = NOW(),
       del_by = #{userId},
       upd_ts = NOW(),
       upd_by = #{userId}
 WHERE grp_cd = #{grp_cd}
   AND cd = #{cd}

UPDATE tb_cd_mst
   SET del_yn = 'Y',
       del_ts = NOW(),
       del_by = #{userId},
       upd_ts = NOW(),
       upd_by = #{userId}
 WHERE grp_cd = #{parent_grp_cd}
   AND cd = #{cd}

UPDATE tb_cd_mst
   SET sort_no = #{sort_no},
       upd_ts = NOW(),
       upd_by = #{userId}
 WHERE grp_cd = #{parent_grp_cd}
   AND cd = #{cd}
